import Vue from 'vue'
import VueRouter from 'vue-router'
import index from './view/index.vue'
import about from './view/about.vue'
import test from './view/test.vue'
import product from './view/product.vue'
import message from './view/message.vue'


Vue.use(VueRouter)

export function createRouter() {
    return new VueRouter({
        mode: 'history', //去掉#
        routes: [
            {
                path: '/',
                component: index,
            },
            {
                path: '/about',
                component: about,
            },
            {
                path: '/test',
                component: test,
            },
            {
                path: '/product',
                component: product,
            },
            {
                path: '/message',
                component: message,
            }
        ]
    })
}

import Vue from 'vue'
import Vuex from 'vuex'
import axios from 'axios'



Vue.use(Vuex)

export function createStore() {
    return new Vuex.Store({
        state: {
            nowString: '???'
        },
        mutations: {
            setString(state, str) {
                state.nowString = str
            }
        },
        actions: {
            getString({ commit, state }, from) {
                //此处必须return返回 asyncData在服务端才会等待promise返回
                //请求路径必须全名,因为可能发生在服务端？？？？  不能用 /api/getString  (这里改变axios默认全局设置)
                //https://blog.csdn.net/qq_27068845/article/details/79382850


                // console.log(state.route.path)  //在客户端这个请求是路由改变前请求的，state.route.path是改变前的path
                //改用传参进入的route
                let url = `/api/getString?pathString=${from.path}`
                return axios.get(url).then((res) => {
                    commit('setString', res.data.str)
                }).catch((err) => {
                    commit('setString', '没有api返回')
                })
            }
        },
    })
}

import Vue from 'vue'
import App from './App.vue'
import { createRouter } from './router'
import { createStore } from './store'
import { sync } from 'vuex-router-sync'   //将router的状态放入store中



export function createApp(context) {
  const router = createRouter(context)
  const store = createStore(context)

  // 同步路由状态     将 route 写入到store的state  //vuex-router-sync
  sync(store, router)

  const app = new Vue({
    store,
    router,
    // 根实例简单的渲染应用程序组件。
    render: h => h(App)
  })

  return { app, router, store }
}

// https://github.com/youngwind/blog/issues/112

import { createApp } from './app'


// // 默认返回匿名函数 context是参数
// export default context => {
//     const { app } = createApp()
//     return app
// }

export default context => {
    return new Promise((resolve, reject) => {
        const { app, router, store } = createApp()

        router.push(context.url)

        router.onReady(() => {
            // 获取router页面的vue组件
            const matchedComponents = router.getMatchedComponents()
            if (!matchedComponents.length) {
                return reject({ code: 404 })
            }

            Promise.all(matchedComponents.map(Component => {
                //如果组件存在asyncData函数
                if (Component.asyncData) {
                    return Component.asyncData({
                        store,
                        route: router.currentRoute
                    })
                }
            })).then(() => {
                console.log('服务端数据预取-------')
                context.state = store.state
                resolve(app)

            }).catch(reject)


        }, reject)
    })
}
